import React,{Component} from 'react';
import ReactDOM from 'react-dom';
import { Form, FormControl } from '..';
//import Form from '../src/form';
//import FormControl from '../src/formcontrol';

require('../assets/index.less');

class Example extends React.Component {

    static PropTypes = {
        prop1: React.PropTypes.string,
        prop2: React.PropTypes.number,
        prop3: React.PropTypes.func,
        prop4: React.PropTypes.oneOf(['blue', 'yellow']),
        prop5: React.PropTypes.oneOfType([
            React.PropTypes.func,
            React.PropTypes.bool
        ])
    };

    static defaultProps = {
        prop1: '',
        prop2: 0,
        prop3: ()=>{},
        prop4: 'yellow',
        props: false
    };

    d = {};

    render() {
        return (
            <Form labelWidth={6} data={this.d}>
                <FormControl name="username" label="用户名" type="text" placeholder="请输入用户名" listen="password"/>
                <FormControl required labelWidth={0} layout={2} name="password" label="密码" type="text" placeholder="请输入密码" password/>
                <FormControl clear labelWidth={0} layout={2} name="password" label="密码" type="text" placeholder="请输入密码" password/>
            </Form>
        )
    }


}

ReactDOM.render(<Example></Example>, document.getElementById('component-example-layout'));




