import React,{Component} from 'react';
import ReactDOM from 'react-dom';
import {Form,FormControl} from '..';
import Button from '@myfe/cat-button';

import '../assets/index.less';

class Example extends React.Component {

    render() {
        return (
            <Form data={{username:'aaa', sex4:1}} onChange={(e)=>console.log(JSON.stringify(e.data))}>
                <FormControl name="username" label="用户名" type="text" placeholder="请输入用户名" required/>
                <FormControl name="password" label="密码" type="number" placeholder="请输入密码" password/>
                <FormControl name="password" label="密码" type="number" placeholder="请输入密码" password disabled/>
                <FormControl name="sex" options={{1:'男',0:'女',all:'全部'}} label="性别" type="checkbox"/>
                <FormControl name="se6" options={{1:'男',0:'女',all:'全部'}} label="性别" type="checkbox" disabled defaultValue={[1]}/>
                <FormControl name="sex2" options={{1:'男',0:'女',all:'全部'}} label="性别" type="radio" required/>
                <FormControl name="sex4" options={{1:'男',0:'女',all:'全部'}} label="性别" type="radio" required disabled/>
                <FormControl name="sex3" options={{1:'男',0:'女',all:'全部'}} label="性别" type="select"/>
                <FormControl name="switch" type="switch" label="switch"/>
                <FormControl name="switch2" type="switch" label="switch" disabled defaultValue={true}/>
                <Button type="submit">提交</Button>
            </Form>
        )
    }
}

ReactDOM.render(<Example />, document.getElementById('component-example-simple'));
