
var Test = React.createClass({

    getInitialState: function() {
        return {value: 0};
    },
    beforeClick() {
        this.setState({
            value: 100
        });
    },
    click: function(){
        this.beforeClick();
        this.state.value = ++this.state.value;
        this.setState(this.state)
    },
    render: function(){
        return (
            <div>
                <div onClick={this.click.bind(this)}>click</div>
                {this.state.value}
            </div>
        )
    }

});


React.render(<Test />, document.body);
