import React,{Component} from 'react';
import ReactDOM from 'react-dom';
import {Form,FormControl} from '..';

import '../assets/index.less';

class Example extends React.Component {

    componentDidMount() {
        window.form = this.refs.form
    }

    render() {
        return (
            <Form data={{username:'aaa'}} onChange={(e)=>console.log(JSON.stringify(e.data))} ref="form">
                <FormControl name="username" label="用户名" type="text" placeholder="请输入用户名" required/>
                <FormControl name="password" label="密码" type="text" placeholder="请输入密码" password/>
                <FormControl name="sex" options={{1:'男',0:'女',all:'全部'}} label="性别" type="checkbox"/>
                <FormControl name="sex2" options={{1:'男',0:'女',all:'全部'}} label="性别" type="radio" disabled required/>
                <FormControl name="sex3" options={{1:'男',0:'女',all:'全部'}} label="性别" type="select"/>
            </Form>
        )
    }
}

ReactDOM.render(<Example />, document.getElementById('component-example-validate'));
