import React from 'react';
import Checkbox from '../editors/checkbox';

export default {
    defaultValue: [],

    renderEditor: function (value, changeHandler) {
        return (
            <Checkbox options={this.props.options}
                      disabled={this.props.disabled}
                      numberValue={this.props.numberValue}
                      value={value}
                      onChange={changeHandler}/>
        );
    },

    pickValue: value => value
}
