import React from 'react';
import Radio from '../editors/radio';

export default {

    renderEditor: function (value, changeHandler) {
        return (
            <Radio options={this.props.options}
                   disabled={this.props.disabled}
                   numberValue={this.props.numberValue}
                   value={value}
                   onChange={changeHandler}/>
        )
    },

    pickValue: value => value

}
