import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Button from '@myfe/cat-button';
import Message from '../src/message.js';
import '../assets/index.less';

class MessageExample extends Component {
	info() {
		Message.info('系统通知: 普通类型');
	}
	success() {
		Message.success('系统通知: 成功类型');
	}
	warning() {
		Message.warning('系统通知: 警告类型');
	}
	danger() {
		Message.danger('系统通知: 失败类型');
	}
    render() {
        return (
			<div>
			  <Button myStyle="info" onClick={this.info}>Info</Button>
			  <Button myStyle="success" onClick={this.success}>Success</Button>
			  <Button myStyle="warning" onClick={this.warning}>warning</Button>
			  <Button myStyle="danger" onClick={this.danger}>danger</Button>
			</div>
			)
    }
}

ReactDOM.render(<MessageExample />, document.getElementById('component-example-different-type'));
