import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Button from '@myfe/cat-button';
import Message from '../src/message.js';
import '../assets/index.less';

class MessageExample extends Component {
	info = (event) => {
		Message.info('系统通知');
	}
	infoInsert = (event) => {
		Message.info('在指定位置显示通知', {container: document.getElementById('message-container')});
	}
    infoInsert2 = (event) => {
        Message.info('在指定位置显示通知', document.getElementById('message-container'));
    }

    render() {
        return (
            <div id="message-container">
                <Button myStyle="info" onClick={this.info}>展现一条普通通知</Button>
                <Button myStyle="info" onClick={this.infoInsert}>在指定位置显示通知</Button>
                <Button myStyle="info" onClick={this.infoInsert2}>在指定位置显示通知</Button>
            </div>
        )
    }
}

ReactDOM.render(<MessageExample />, document.getElementById('component-example-user-defined-container'));

