import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Button from '@myfe/cat-button';
import Message from '../src/message.js';
import '../assets/index.less';

class MessageExample extends Component {
	info = (event) => {
		Message.info('普通系统通知');
	}
    infoDefine = (event) => {
		Message.info('十秒后才关闭的通知', 10);
    }
	infoLive = (event) => {
		Message.info('需要手动关闭的通知', 0);
	}

    render() {
        return (
            <div>
                <Button myStyle="info" onClick={this.info}>展现一条普通通知</Button>
                <Button myStyle="info" onClick={this.infoDefine}>自定时长通知</Button>
                <Button myStyle="info" onClick={this.infoLive}>需手动关闭通知</Button>
            </div>
        )
    }
}

ReactDOM.render(<MessageExample />, document.getElementById('component-example-user-defined-time'));

