import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Tree from '../src/tree.js';
import DATA from './data';

class Example extends Component {

    constructor(props) {
        super(props);
    }
    
    state = {
        data: [],
        selected: '/pages/price/poidetail.jsp'
    };

    onClick = () => {
        this.setState({
            data: DATA[Math.floor(Math.random() * DATA.length)].children
        });
    }

    render() {
        return (
            <div>
                <button className="btn btn-primary btn-small" onClick={this.onClick}>切换数据</button>
                <Tree {...this.state} dynamic={true}/>
                <hr />
                <Tree {...this.state} dynamic={true}/>
            </div>          
        );
    }
}

ReactDOM.render(<Example />, document.getElementById('component-example-data-dymanic'));
