import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { PagePortal } from '../index.js';
import Pop from '@myfe/cat-pop';
import "../assets/index.less";
import getSideInfo from './get-side-info.js';

class Example extends PagePortal {
    user = {
        name: 'yanweiqing'
    }

    system = {
        name: '猫眼电影' 
    }

    current = getSideInfo('/');

    // User Defined Render
    renderHeaderContent() {
        return (
            <ul className="nav navbar-nav pull-left">
                <li>
                    <a target="_blank" href="">
                        <Pop content="使用问题反馈" place="bottom">
                            <i className="glyphicon glyphicon-question-sign"></i>
                        </Pop>
                    </a>
                </li>
            </ul>
        );
    }
}

class Container extends Component {
    render() {
        return (
            <div style={{position: 'relative'}}>
                <Example />
            </div>
        );
    }
}

ReactDOM.render(<Container />, document.getElementById('component-example-base-pageportal'));
