import React from 'react';
import Header from '../layout/header.jsx';
import Footer from '../layout/footer.jsx';
import SideBar from '../layout/side-bar.jsx';
import HeaderBread from '../layout/header-bread.jsx';
import HeaderTitle from '../layout/header-title.jsx';
import BasePage from './page-base';

export default class PageLayout extends BasePage {

    renderHeaderContent() {}

    renderHeader() {
        let Component = this.hasBreadCrumb ? HeaderBread : HeaderTitle;

        return <Component {...this.current} />;
    }

    render() {
        return (
            <div>
                <Header user={this.user} system={this.system} renderHeaderContent={this.renderHeaderContent}/>
                <SideBar {...this.current} hasChannel={this.hasChannel}/>
                <div className="my-page-content">
                    <div className="my-page-header">
                        {this.renderHeader()}
                    </div>
                    <div className="my-page-top-bar">
                        {this.renderTopBar()}
                    </div>
                    <div className="my-page-main">
                        {this.renderMain()}
                        {this.renderMainExtra()}
                    </div>
                    <div className="my-page-bottom-bar">
                        {this.renderBottomBar()}
                    </div>
                    <div className="my-page-footer">
                        {this.renderFooter()}
                    </div>
                </div>
                {
                    this.hasFooter && <Footer system={this.system} />
                }
            </div>
        );
    }
}
