import React from 'react';
import BreadCrumb from '@myfe/cat-breadcrumb';

export default class HeaderBread extends React.Component {
    static propTypes = {
        breads: React.PropTypes.array
    };

    static defaultProps = {
        breads: []
    };

    render() {
        let { breads } = this.props;

        return (
            <BreadCrumb>
                {
                    _.map(breads, (bread) => {
                        return <BreadCrumb.Crumb href={bread.href} key={bread.name}>{bread.name}</BreadCrumb.Crumb>
                    })
                }
            </BreadCrumb>
        );
    }
}
