import React, { Component } from 'react';

export default class Pre extends Component {

    handleClick(index) {
        let event = this.props.handleSelect;
        if (event) {
            event(index);
        }
    }
    render() {
        let { dataList } = this.props;
        let len = dataList.length;
        return (
            <dl className="cat-prelist text-center clearfix">
                {
                    dataList.map((item, index) => {
                        return (
                            <div key={index} style={{width: (100/len) + '%'}} onClick={this.handleClick.bind(this, item.key ? item.key : index)}>
                                <dt>{item.title}</dt>
                                <dd>{item.content}</dd>
                            </div>
                        );
                    })
                }
            </dl>
        );
    
    }
}
