import React, { Component } from 'react';
import SlideMenu from './slide-menu';
import SlideChannel from './slide-channel';

export default class SideBar extends Component {
    static propTypes = {
        current: React.PropTypes.string, //当前页面的path
        currentMenuData: React.PropTypes.array,
        channel: React.PropTypes.object,
        hasChannel: React.PropTypes.bool
    };

    static defaultProps = {
        current: '/dashboard',
        currentMenuData: [],
        channel: {},
        hasChannel: false
    };

    // bar和menu之间还多一层，因为bar之中的组件不仅仅是menu
    render() {
        let { current, currentMenuData, channel, hasChannel } = this.props;

        return (
            <div className="side-bar">
                {
                    hasChannel && <SlideChannel channel={channel} />
                }
                <SlideMenu data={currentMenuData} selected={current} active={current}/>
            </div> 
        )
    }
}
