import React, { Component } from 'react';
import Dropdown from '@myfe/cat-dropdown';

export default class SlideChannel extends Component {

    static propTypes = {
        channel: React.PropTypes.object
    };

    static defaultProps = {
        channel: {}
    };

    render() {
        let { channel } = this.props,
            { name, channelList } = channel;

        return (
            <div className="slide-channel">
                <Dropdown
                    title={name}
                    myStyle="primary"
                    liveKey={name}
                    trigger="click"
                    block
                >
                    {
                        _.map(channelList, item => {
                            return (
                                <Dropdown.Item
                                    eventKey={item.name}
                                    key={item.name}
                                    href={item.href}
                                >
                                    {item.name}
                                </Dropdown.Item>
                            )
                        })
                    }
                </Dropdown>
            </div>
        )
    }
}


