import React from 'react';
import Header from '../layout/header.jsx';
import Footer from '../layout/footer.jsx';
import BasePage from '../base/page-base';

export default class PagePortal extends BasePage {

    renderHeaderContent() {}

    renderHeader() {
        return <Header user={this.user} system={this.system} renderHeaderContent={this.renderHeaderContent}/>;
    }

    _renderEntry(item) {
        return (
            <a href={item.href}>
                <i className={item.icon}></i>
                <h2>{item.name}</h2>
            </a>
        )
    };

    renderMain() {
        let dataList = this.current;
        return (
            <div className="container">
                <ul className="row text-center portal-list">
                    {
                        _.map(dataList.currentMenuData, item => {
                            return (
                                <li
                                    className="col-sm-6"
                                    key={item.name}
                                >
                                    {this._renderEntry(item)}
                                </li>
                            )
                        })
                    }
                </ul>
            </div>
        );
    }

    renderFooter() {
        return <Footer system={this.system} className="common-footer-dashboard" /> ;
    }

}
